import axios from 'axios'
import React,{useState,useEffect} from 'react'
import { Link,useParams } from 'react-router-dom'

const Rickmorty = () => {
    const [usuario, setUsuario] = useState([])
    const {id} = useParams()

    const obtenerUsuario = async () => {
        const res = await axios.get(`https://rickandmortyapi.com/api/character/${id}`)
        const data = await res.data
        console.log(data)
        setUsuario(data)
    }

    useEffect(() => {
        obtenerUsuario()
    }, [])

  return (
    

    <div className="row">
      <div className="col my-3" key={usuario.id}>
        <>
          <div className="card">
            <img src={usuario.image} alt=" " width={200} />
            <div className="card-body">
              <h5 className="card-title">{usuario.name}</h5>
              <p className="card-text">
                {usuario.status}
                <br />
                {usuario.species}
                <br />
                {usuario.gender}
                <br />
                <strong>Location: </strong>
                {usuario.location.name}
              </p>
            </div>
          </div>
        </>
      </div>
      <Link to='/rick/RickAndMorty' className="btn btn-primary mt-5">Regresar</Link>
    </div>
  )
}

export default Rickmorty
